import React from "react";

const Card = ({ post }) => {
  const date = post?.created_at.split(" ")[0];

  Date.prototype.addDays = function (days) {
    const date = new Date(this.valueOf());
    date.setDate(date.getDate() + days);
    return date;
  };
  const newDate = new Date(date);
  const seminarDate = newDate;

  var datestring =
    seminarDate.getDate() +
    "/" +
    (seminarDate.getMonth() + 1) +
    "/" +
    seminarDate.getFullYear() +
    " " +
    seminarDate.getHours() +
    ":" +
    seminarDate.getMinutes();

  console.log(seminarDate);
  return (
    <div className="mx-5 my-2 p-4 h-[300px] flex flex-col justify-between  border border-sm bg-white">
      <span className="text-[#01254F] text-sm my-2">{date}</span>
      <h1 className="text-base my-5 text-[#01254F] font-semibold w-[280px] ">
        {post?.title}
      </h1>
      <p className="text-sm text-gray-700 mb-5 truncate ... hover:text-clip">
        <div className="" dangerouslySetInnerHTML={{ __html: post?.content }} />
      </p>
      <p className="text-sm text-gray-700 mb-5">
        {/* {seminarDate != "Invalid Date" ? seminarDate : ""} */}
        {seminarDate}
      </p>
      <div className="text-[#6BB718]">
        <span>Register Now</span>
      </div>
    </div>
  );
};

export default Card;
